import React, { useState, useEffect } from "react";

// Fucntional Counter Component
const FunctionalCounter = () => {
  const [count, setCount] = useState(0);

  useEffect(() => {
    document.title = `Func update ${count}`;
  });

  return (
    <div className="counter">
      <h2>ນີ້ແມ່ນ Functional counter</h2>
      <p>ຈຳນວນ: {count}</p>
      <button
        className="add-btn"
        onClick={() => {
          setCount(count + 1);
        }}
      >
        ເພີ່ມ
      </button>
      <button
        className="del-btn"
        onClick={() => {
          setCount(count - 1);
        }}
      >
        ລົບ
      </button>
    </div>
  );
};

// Classs Component
class ClassCounter extends React.Component {
  constructor(props) {
    super(props);
    this.state = { count: 5 };
  }

  componentDidUpdate() {
    document.title = `ຈຳນວນ: ${this.state.count}`;
  }
  render() {
    return (
      <div className="counter">
        <h2>ນີ້ແມ່ນ Class counter</h2>
        <p>ຈຳນວນ: {this.state.count}</p>
        <button
          className="add-btn"
          onClick={() => {
            this.setState({
              count: this.state.count + 1,
            });
          }}
        >
          ເພີ່ມ
        </button>
        <button
          className="del-btn"
          onClick={() => {
            // this.props.count = 1;
            this.setState({
              count: this.state.count - 1,
            });
          }}
        >
          ລົບ
        </button>
      </div>
    );
  }
}

